package week1;

public class A8Perimeter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		//Perimeter Of Circle
		int r1= 3;
		double a1=2.0*3.14*r1;
		System.out.println("Perimeter Of Circle : " +a1);
		
		
		//Perimeter Of Equilateral Triangle
		int s2=3;
		int a2= 3*s2;
		System.out.println("Perimeter Of Equilateral Triangle: " +a2);
		
		
		//Perimeter Of Parallelogram
		int l3=3;
		int b3=4;
		int a3= 2*(l3+b3);
		System.out.println("Perimeter Of Parallelogram: " +a3);
		
		
		//Perimeter Of Rectangle
		int l4=3;
		int b4=4;
		int a4= 2*(l4+b4);
		System.out.println("Perimeter Of Rectangle: " +a4);
		
		
		//Perimeter Of Square
		int s5=3;
		int a5= 4*s5;
		System.out.println("Perimeter Of Square: " +a5);
	
	
		// Perimeter Of Rhombus
		int s6=3;
		int a6= 4*s6;
		System.out.println("Perimeter Of Rhombus: " +a6);
	
	
	}

}
